const behavior = object.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));
object.__isFlipped = behavior._getAttackDirection() === "Left";

const object = objects[0];
const character = eventsFunctionContext.getObjects("Character")[0];
const behavior = object.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));
const point = eventsFunctionContext.getArgument("Point");

if (character !== undefined) {
    behavior.heldBy = character;
    behavior.point = point;
    behavior.heldByAngle = character.getAngle();
    behavior.objectAngle = object.getAngle();
    behavior.isRotationEnabled = behavior._getFollowRotation();
    object.setCenterPositionInScene(character.getPointX(point), character.getPointY(point));
}

const object = objects[0];
const behavior = object.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));

behavior.heldBy = null;
behavior.point = null;

const object = objects[0];
const behavior = object.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));

if ((behavior.heldBy !== undefined) && (behavior.heldBy !== null) && 
    (behavior.point !== undefined) && (behavior.point !== null)) {
    object.setCenterPositionInScene(
        behavior.heldBy.getPointX(behavior.point), 
        behavior.heldBy.getPointY(behavior.point));

    if (behavior.isRotationEnabled) {
        const rotationDifference = behavior.heldBy.getAngle() - behavior.heldByAngle;
        const newAngle = behavior.objectAngle + rotationDifference;
        if (behavior.heldBy.__isFlipped) {
            object.setAngle(-newAngle);
        }
        else {
            object.setAngle(newAngle);
        }
    }
}

const object = objects[0];
const behavior = object.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));
const handheldBehavior = object.getBehavior(eventsFunctionContext.getBehaviorName("HandheldBehavior"));
const swingArc = behavior._getSwingArc();
let startingAngle = behavior._getStartingAngle();
let swingSpeed = behavior._getSwingSpeed();

if (handheldBehavior.isRotationEnabled) {
    handheldBehavior.isRotationEnabled = false;
    behavior.isRotationEnabled = true;
    object.setAngle(startingAngle);
}

if (behavior.isRotationEnabled) {
    startingAngle += (handheldBehavior.heldBy.getAngle() - handheldBehavior.heldByAngle);
    if (handheldBehavior.heldBy.__isFlipped) {
        startingAngle = -startingAngle;
        swingSpeed = -swingSpeed;
    }
    if ((startingAngle != object.getAngle()) && !behavior._getIsSwinging()) {
        object.setAngle(startingAngle);
    }
}

if (behavior._getIsSwinging()) {
    if (!behavior.isSwingStarted) {
        behavior.isSwingStarted = true;
        behavior.isSwingFinished = false;
        object.setAngle(startingAngle);
        behavior.swingArc = 0;
    }
    else if (behavior.swingArc < swingArc && !behavior.isSwingFinished) {
        object.setAngle(object.getAngle() + swingSpeed);
        behavior.swingArc += Math.abs(swingSpeed);
    }
    else if (behavior.swingArc >= swingArc && !behavior.isSwingFinished) {
        behavior.isSwingFinished = true;
    } 
    else if (behavior.isSwingFinished && behavior.swingArc > 0) {
        object.setAngle(object.getAngle() - swingSpeed);
        behavior.swingArc -= Math.abs(swingSpeed);
    }
    else {
        object.setAngle(startingAngle);
        behavior.isSwingStarted = false;
        behavior.isSwingFinished = false;
        behavior._setIsSwinging(false);
    }
}
