const object = objects[0];
const behavior = object.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));

if (!behavior.isSwingStarted) {
    behavior.isSwingStarted = true;
    object.setAngle(behavior._getStartingAngle());
    behavior.swingArc = 0;
}
else if (behavior.swingArc < behavior._getSwingArc()) {
    object.setAngle(object.getAngle() + behavior._getSwingSpeed());
    behavior.swingArc += Math.abs(behavior._getSwingSpeed())
}
else if (behavior.swingArc > 0) {
    object.setAngle(object.getAngle() - behavior._getSwingSpeed());
    behavior.swingArc -= Math.abs(behavior._getSwingSpeed);
}
else {
    object.setAngle(behavior._getStartingAngle());
    behavior.isSwingStarted = false;
    behavior._setIsSwinging(false);
}
